Scripts.OnClick:
 self:GetParent():Hide(); 
							if self:GetParent().func then
								if type(self:GetParent().arg) == "table" then
									self:GetParent().func(self:GetParent(), unpack(self:GetParent().arg));
								else
									self:GetParent().func(self:GetParent(), self:GetParent().arg);
								end
							end

 self:GetParent():Hide(); 
							if self:GetParent().func then
								if type(self:GetParent().arg) == "table" then
									self:GetParent().func(self:GetParent(), unpack(self:GetParent().arg));
								else
									self:GetParent().func(self:GetParent(), self:GetParent().arg);
								end
							end

Scripts.OnEnter:
local visibleWidth = self.Text:GetWidth()
local fullWidth = self.Text:GetUnboundedStringWidth()
if fullWidth > visibleWidth then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:AddLine(self.Text:GetText());
	GameTooltip:Show();
end
